  const [datos, actualizaDatos ] = React.useState(null); 
  React.useEffect(() => {
    console.log('ðŸ¦§ðŸ¤“');
    d3.csv(csvUrl).then(data => {
      actualizaDatos(data[500]);
      console.log(data[500]);
    });
  }, []); 
  return (<text fill="black" x={width/2} y={height/2} > 
          {(datos)?datos.nombre + " " + datos.poblacion: "Cargando"} 
          </text>);
};


function App() {
